import javafx.application.Application;import javafx.geometry.Pos;import javafx.geometry.Insets;import javafx.scene.control.Button;import javafx.scene.layout.GridPane;import javafx.scene.Scene;import javafx.stage.Stage;/** * Same as TicTacToe, but DRY. */public class TicTacToe2 extends Application {    private boolean xTurn = true;    private Button mkButton() {        Button button = new Button();        button.setOnAction(e -> {                button.setText(xTurn ? "X" : "O");                xTurn = !xTurn;                button.setDisable(true);            });        return button;    }    @Override public void start(Stage stage) {        GridPane grid = new GridPane();        grid.setAlignment(Pos.CENTER);        grid.setHgap(10);        grid.setVgap(10);        grid.setPadding(new Insets(25, 25, 25, 25));        for (int col = 0; col < 3; col++) {            for (int row = 0; row < 3; row++) {                grid.add(mkButton(), col, row);            }        }        Scene scene = new Scene(grid);        stage.setScene(scene);        stage.setTitle("Tic Tac Toe");        stage.show();    }}